qrList.message:
/if left=board right=user rule=eq "/run battlestripsv2.charselecttarget" |
/if left=looping right=false rule=eq "/abort" |

/input <div class='maincont'>
<style>
.user{background-color: {{getvar::usergridcolor}}
}
.char{background-color: {{getvar::chargridcolor}}
}
.MISS{background-color: {{getvar::missgridcolor}}
}
.HIT{background-color: {{getvar::hitgridcolor}}
}
.maincont{display: flex;
flex-direction: column;
padding: 5px;
width: 400px;
gap: 10px
}
.board{padding: 1px;
border: 1px solid green;
display: inline-grid;
gap: 1px;
grid-template-columns: 1fr 1fr 1fr 1fr 1fr
}
.grid{border: 1px solid white;
border-radius: 2px;
width: 35px;
height: 35px;
display: flex;
justify-content: center;
align-items: center;
padding: 1px;
font-size: 10px
}
.info{width: 40%;
font-size: 10px;
text-wrap: wrap
}
.playercont{display: flex;
text-align: left;
justify-content: center;
gap: 5px
}
</style>

<div class='playercont'>
{{getvar::charboardhtml}}
<div class='info'>
<b>{{getvar::girlname}}'s board</b><br>
<hr>
Your last shot: {{getvar::userlast}}
</div>
</div>

<div class='playercont'>
{{getvar::userboardhtml}}
<div class='info'>
<b>Your board</b><br>
<hr>
Her last shot: {{getvar::charlast}}
</div>
</div>

</div>

<div style='text-align: left; font-size: 10px; color: grey'>
Input target grid. Ex: D3 or a4<br>
Input <font color=red>giveup</font> to end the game.
</div> |

/setvar key=tmpinputgrid |

/if left=tmpinputgrid right=giveup rule=eq "/run battlestripsv2.giveup \| /abort" |

/if left=board right=char rule=eq "/run battlestripsv2.validateinput"
