/input #{{getvar::pass}}<script type="text/javascript">
setTimeout((() => {
//essentials
//get the popup components we can play with
const popup = document.querySelector("#dialogue_popup_holder");
const popupHeader = popup.querySelector("#dialogue_popup_text");
const popupInput = popup.querySelector("#dialogue_popup_input");
const popupControls = popup.querySelector("#dialogue_popup_controls");
const okButton = popupControls.querySelector("#dialogue_popup_ok");
const cancelButton = popupControls.querySelector("#dialogue_popup_cancel");

//output setter
const setReturnValue = (val) => {
  popupInput.value = val;
};

//hide/show popup elems - only select: popupHeader, popupInput, okButton, cancelButton
const hidePopupElems = (elems) => {
  elems.forEach((elem) => {
    elem.style.visibility = "hidden"
    elem.style.position = "absolute"
  })
};
const showPopupElems = (elems) => {
  elems.forEach((elem) => {
    elem.style.visibility = "visible"
    elem.style.position = "static"
  })
};

const mainCont = document.querySelector("#main");
const selections = mainCont.querySelector("#selections");
const bulkSelectors = mainCont.querySelector("#bulk_selectors");
const selectAll = bulkSelectors.querySelector("#select_all");
const deselectAll = bulkSelectors.querySelector("#deselect_all");
const links = {
  {{getvar::links}}
};
const presets = Object.keys(links);

const initSelections = () => {
  const optionsDisp = presets.map((option) => `<div class="selection">
  <input type="checkbox" id="${option}" name="${option}" value="${option}" checked>
  <label for="${option}">${option}</label>
</div>`
).join('')

  selections.innerHTML = optionsDisp;
}

const initSelectionsEvents = () => {
  presets.forEach((preset) => {
    const selection = selections.querySelector(`#${preset}`)
    selection.addEventListener("change", onCheckboxChange)
  })
}

let toUpdate = Object.keys(links);
const onCheckboxChange = (e) => {
  if(e.srcElement.checked) {
    toUpdate.push(e.srcElement.value);
  }
  else {
    toUpdate = toUpdate.filter((preset) => preset !== e.srcElement.value)
  }

  setReturnValue(JSON.stringify(toUpdate));
}

const main = () => {
  hidePopupElems([popupInput]);

  setReturnValue(JSON.stringify(toUpdate));
  initSelections();
  initSelectionsEvents();

  okButton.addEventListener("click", (e) => {
    showPopupElems([popupHeader, popupInput, okButton, cancelButton])
  });
  cancelButton.addEventListener("click", (e) => {
    showPopupElems([popupHeader, popupInput, okButton, cancelButton])
  });

  selectAll.addEventListener("click", (e) => {
    presets.forEach((preset) => {
      const selection = selections.querySelector(`#${preset}`)
      selection.checked = true;
      toUpdate = Object.keys(links);
      setReturnValue(JSON.stringify(toUpdate));
    })
  });
  deselectAll.addEventListener("click", (e) => {
    presets.forEach((preset) => {
      const selection = selections.querySelector(`#${preset}`)
      selection.checked = false;
      toUpdate = [];
      setReturnValue(JSON.stringify(toUpdate));
    })
  });
};

main();

})(), 0);
</script>

<h3>Select presets to update.</h3>
<div id="main">

