/input #{{getvar::pass}}<script type="text/javascript">
(() => {setTimeout(() => {

//essentials
//get the popup components we can play with
const popup = document.querySelector("#dialogue_popup_holder");
const popupHeader = popup.querySelector("#dialogue_popup_text");
const popupInput = popup.querySelector("#dialogue_popup_input");
const popupControls = popup.querySelector("#dialogue_popup_controls");
const okButton = popupControls.querySelector("#dialogue_popup_ok");
const cancelButton = popupControls.querySelector("#dialogue_popup_cancel");

//output setter
const setReturnValue = (val) => {
  popupInput.value = val;
};

//hide/show popup elems - only select: popupHeader, popupInput, okButton, cancelButton
const hidePopupElems = (elems) => {
  elems.forEach((elem) => {
    elem.style.visibility = "hidden"
    elem.style.position = "absolute"
  })
};
const showPopupElems = (elems) => {
  elems.forEach((elem) => {
    elem.style.visibility = "visible"
    elem.style.position = "static"
  })
};


let userInfoArr = {{getvar::userinfoarr}}
let aiResponse = `{{getvar::aimsg}}`
let toEcho = "No info added"

const addInfoToArr = () => {
  userInfoArr = userInfoArr.filter(item => /None so far/i.test(item) === false)

  if(/replace\(/.test(aiResponse)) {
    //replace command
    let newInfo = (aiResponse.match(/(?<=replace\(\d+?,\s)[\S\s]+(?=\))/i))[0]
    let lineNum = (aiResponse.match(/(?<=replace\()\d+?(?=,)/i))[0]
    lineNum -= 1
    let oldInfo = userInfoArr[lineNum]
    toEcho = `Replaced "${oldInfo}" with "${newInfo}"`
    userInfoArr[lineNum] = newInfo
  }
  else if(/add\(/.test(aiResponse)) {
    //add command
    let newInfo = (aiResponse.match(/(?<=add\()[\S\s]+(?=\))/i))[0]
    toEcho = `Added "${newInfo}"`
    userInfoArr.push(newInfo)
  }
}

const genOutput = () => {
  let lorebookInfo
  for(let i = 0; i < userInfoArr.length; i++) {
    if(lorebookInfo === undefined) {
      lorebookInfo = `${i + 1}. ${userInfoArr[i]}`
      continue
    }
    lorebookInfo = `${lorebookInfo}
${i + 1}. ${userInfoArr[i]}`
  }

  if(lorebookInfo === undefined) {
    lorebookInfo = "1. None so far"
  }

  let output = {
    "toEcho": toEcho,
    "toUserInfoArr": userInfoArr,
    "toLorebook": `<{{user}}>
What Sini knows about {{user}}:
${lorebookInfo}
</{{user}}>`
  }

  setReturnValue(JSON.stringify(output))
}


const main = () => {
  hidePopupElems([popupInput, cancelButton]);

  addInfoToArr()
  genOutput()

  okButton.addEventListener("click", (e) => {
    showPopupElems([popupHeader, popupInput, okButton, cancelButton])
  });
  cancelButton.addEventListener("click", (e) => {
    showPopupElems([popupHeader, popupInput, okButton, cancelButton])
  });

  let ev = new Event("click")
  okButton.dispatchEvent(ev)
};

main();

}, 0)})();
</script>

