	  /mul -1 enemyspeed |
	  /setvar key="enemy{{getvar::i}}speed" |
	  /setvar key=enemyspeed |
	  /add {{pipe}} enemyangle |
	  /setvar key=newangle
	:} |

	/pass enemy{{getvar::i}}smoke |
	/if left={{pipe}} right=hidden rule=eq {:
	  /setvar key="enemy{{getvar::i}}currangle" {{getvar::newangle}}
	:}
:} |


/let hitenemy {:
	/setvar key=ishit true |
	/addvar key=i 1 |

	/pass enemy{{getvar::i}}currangle |
