	/mul -1 precision | /add {{pipe}} enemyangle |
	/setvar key=minanglehit |
	/add precision enemyangle |
	/setvar key=maxanglehit |

	/if left=angle right=minanglehit rule=lt {: /setvar key=ishit false :} |
	/if left=angle right=maxanglehit rule=gt {: /setvar key=ishit false :} |
	/if left=ishit right=false rule=eq {: /break :} |

	/pass enemy{{getvar::i}}smoke |
	/if left={{pipe}} right=hidden rule=eq {:
	  /setvar key="enemy{{getvar::i}}smoke" visible |
	  /setvar key="enemy{{getvar::i}}speed" 0 |
	  /addvar key=enemyremaining -1 |
	  /addvar key=hitsinarow 1 |
	  /echo Nice shot!
	:} |

