	/re-replace find=/[^a-z\d\-]/gi replace={{noop}} {{getvar::tempangle}} |
	/setvar key=tempangle |

	/if left=tempangle right=surrender rule=eq {:
	  /setvar key=angle 0 | /setvar key=hp 0
	:} |

