  /re-test find=/<details>\s*<summary>\s*Original\s*</summary>\s*\|\s*</details>/gi {{lastMessage}} |
  /if left={{pipe}} right=true rule=eq else={:
  	/cut {{lastMessageId}}
  :}
  {:
