const memo = (key, value) => {
  if(value === "") {
    return vals[key]
  }
  else {
    vals[key] = value
    return value
  }
}


let unusedPool = {}
const pool = (entity, value) => {
  if(value === "") {
    return unusedPool[entity].pop()
  }
  else {
    if(Object.keys(unusedPool).includes(entity)) {
      unusedPool[entity].push(value)
    }
    else {
      unusedPool[entity] = []
      unusedPool[entity].push(value)
    }
    return value
  }
}


const saveState = () => {
