  //distance to goal
  distRemain = Math.round(Math.sqrt(getDistRaw(goal)))
  if(distRemain <= HAVEN_REACHED_DIST) {
    endGame(true)
  }

  let weather = "No blizzard";
  switch(playerVisibility) {
    case 150: weather = "No blizzard"; break;
    case 80: weather = "Moderate blizzard"; break;
